mapper.select:
  select 

	 from m_merchant_core


     AND inner_code = #{condition.innerCode,jdbcType=VARCHAR}


     AND mer_name LIKE CONCAT('%', #{condition.merName}, '%')


     AND abbreviation = #{condition.abbreviation,jdbcType=VARCHAR},


     AND en_name = #{condition.enName,jdbcType=VARCHAR}


     AND sign_date = #{condition.signDate,jdbcType=VARCHAR}


     AND legal_person LIKE CONCAT('%', #{condition.legalPerson}, '%')


     AND legal_person_mobile = #{condition.legalPersonMobile,jdbcType=VARCHAR}


     AND legal_person_tel = #{condition.legalPersonTel,jdbcType=VARCHAR}


     AND legal_valid_card_type = #{condition.legalValidCardType,jdbcType=VARCHAR}


     AND card_num = #{condition.cardNum,jdbcType=VARCHAR}


     AND card_valid_time = #{condition.cardValidTime,jdbcType=VARCHAR}


     AND business_license_num = #{condition.businessLicenseNum,jdbcType=VARCHAR}


     AND business_license_valid_time = #{condition.businessLicenseValidTime,jdbcType=VARCHAR}


     AND tax_regist_code = #{condition.taxRegistCode,jdbcType=VARCHAR}


     AND regist_address = #{condition.registAddress,jdbcType=VARCHAR}


     AND merc_flag = #{condition.mercFlag,jdbcType=VARCHAR}


     AND source = #{condition.source,jdbcType=INTEGER}


     AND modify_user_id = #{condition.modifyUserId,jdbcType=VARCHAR}


     AND modify_time = #{condition.modifyTime,jdbcType=TIMESTAMP}

     AND status = 1

   ORDER BY modify_time DESC
   limit #{startRow} , #{perPageSize}

