mapper.select:
select 

  from sys_msg_app


    AND send_time = #{condition.sendTime,jdbcType=TIMESTAMP}


    AND content = #{condition.content,jdbcType=VARCHAR}


    AND phone_type = #{condition.phoneType,jdbcType=INTEGER}


    AND send_type = #{condition.sendType,jdbcType=INTEGER}


    AND push_type = #{condition.pushType,jdbcType=INTEGER}


    AND status = #{condition.status,jdbcType=INTEGER}


    AND modify_user_id = #{condition.modifyUserId,jdbcType=INTEGER}


    AND modify_time = #{condition.modifyTime,jdbcType=TIMESTAMP}


    AND msg_type = #{condition.msgType,jdbcType=INTEGER}


    AND bus_type = #{busType,jdbcType=INTEGER}


 ORDER BY modify_time DESC 
 limit #{startRow} , #{perPageSize}

select count(*)  from sys_msg_app


    AND send_time = #{sendTime,jdbcType=TIMESTAMP}


    AND content = #{content,jdbcType=VARCHAR}


    AND phone_type = #{phoneType,jdbcType=INTEGER}


    AND send_type = #{sendType,jdbcType=INTEGER}


    AND push_type = #{pushType,jdbcType=INTEGER}


    AND status = #{status,jdbcType=INTEGER}


    AND modify_user_id = #{modifyUserId,jdbcType=INTEGER}


    AND modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    AND msg_type = #{msgType,jdbcType=INTEGER}


    AND bus_type = #{busType,jdbcType=INTEGER}
