  )
values (#{id,jdbcType=INTEGER}, #{code,jdbcType=VARCHAR}, #{bankName,jdbcType=VARCHAR}, 
  #{provinceId,jdbcType=INTEGER}, #{provinceName,jdbcType=VARCHAR}, #{cityId,jdbcType=INTEGER}, 
  #{cityName,jdbcType=VARCHAR}, #{branchBankName,jdbcType=VARCHAR}, #{anBankId,jdbcType=VARCHAR}
