mapper.select:
  select 

  from sys_bank


     AND code = #{condition.code,jdbcType=VARCHAR}


     AND bank_name = #{condition.bankName,jdbcType=VARCHAR}


     AND  province_id = #{condition.provinceId,jdbcType=INTEGER}


     AND  province_name = #{condition.provinceName,jdbcType=VARCHAR}


     AND  city_id = #{condition.cityId,jdbcType=INTEGER}


      AND city_name = #{condition.cityName,jdbcType=VARCHAR}


      AND branch_bank_name = #{condition.branchBankName,jdbcType=VARCHAR}


     AND  an_bank_id = #{condition.anBankId,jdbcType=VARCHAR}


	limit #{startRow} , #{perPageSize}

	select count(*) 
		from sys_bank


     AND code = #{code,jdbcType=VARCHAR}


     AND bank_name = #{bankName,jdbcType=VARCHAR}


     AND  province_id = #{provinceId,jdbcType=INTEGER}


     AND  province_name = #{provinceName,jdbcType=VARCHAR}


     AND  city_id = #{cityId,jdbcType=INTEGER}


