mapper.insert:
insert into r_trade_by_day (id, trade_date, inner_code, 
  turnover, order_num, order_price
  )
values (#{id,jdbcType=INTEGER}, #{tradeDate,jdbcType=VARCHAR}, #{innerCode,jdbcType=VARCHAR}, 
  #{turnover,jdbcType=DECIMAL}, #{orderNum,jdbcType=INTEGER}, #{orderPrice,jdbcType=DECIMAL}
  )

mapper.select:
select SUM(turnover) AS turnover,SUM(order_num) AS orderNum from r_trade_by_day


    AND trade_date >= #{startTradeDate}


    AND trade_date <= #{endTradeDate}


    AND trade_date= #{tradeDate}


   AND inner_code in (
     SELECT inner_code FROM m_merchant_user_rel WHERE app_user_id=#{userId}
   )


   AND inner_code = #{innerCode}

select SUM(turnover) AS turnover,SUM(order_num) AS orderNum from r_trade_by_day


    AND trade_date >= #{startTradeDate}


    AND trade_date <= #{endTradeDate}


    AND trade_date= #{tradeDate}


   AND inner_code in (
     SELECT inner_code FROM u_app_user_merchant WHERE role_id=#{roleId} AND app_user_id=#{userId}
   )

SELECT trade_date AS tradeDate,order_num AS orderNum, turnover AS turnover FROM r_trade_by_day 


   AND trade_date >= #{startTradeDate}


   AND trade_date <= #{endTradeDate}


   AND inner_code = #{innerCode}
