mapper.select:
   SELECT SUM(turnover) AS turnover,SUM(order_num) AS orderNum,pay_type AS payType 
         FROM r_trade_by_pay_type 


        AND inner_code = #{innerCode}


       AND  trade_date >= #{startDate}


       AND trade_date <= #{endDate}


       AND inner_code in (
         SELECT inner_code FROM u_app_user_merchant 
           WHERE app_user_id =#{userId} 

           AND role_id =#{roleId}

       )


	  GROUP BY pay_type
