mapper.select:
  select 

	 from m_merchant_entity


     and entity_inner_code = #{condition.entityInnerCode,jdbcType=VARCHAR}


     and merc_name like CONCAT('%', #{condition.mercName,jdbcType=VARCHAR},'%')


     and legal_person = #{condition.legalPerson,jdbcType=VARCHAR}


     and legal_person_mobile = #{condition.legalPersonMobile,jdbcType=VARCHAR}


     and business_license_num = #{condition.businessLicenseNum,jdbcType=VARCHAR}


     and card_type = #{condition.cardType,jdbcType=VARCHAR}


     and card_num = #{condition.cardNum,jdbcType=VARCHAR}


     and create_timer = #{condition.createTimer,jdbcType=TIMESTAMP}


     and create_source = #{condition.createSource,jdbcType=VARCHAR}


     and create_user_id = #{condition.createUserId,jdbcType=INTEGER}


     and last_modefy_timer = #{condition.lastModefyTimer,jdbcType=TIMESTAMP}


     and last_modefy_user_id = #{condition.lastModefyUserId,jdbcType=INTEGER}


     and scores = #{condition.scores,jdbcType=DECIMAL}

     and status = 1

   ORDER BY id DESC
   limit #{startRow} , #{perPageSize}

select count(*)  from 
		m_merchant_entity


   and entity_inner_code = #{entityInnerCode,jdbcType=VARCHAR}


  and  merc_name like CONCAT('%',#{mercName,jdbcType=VARCHAR},'%')


   and legal_person = #{legalPerson,jdbcType=VARCHAR}


   and legal_person_mobile = #{legalPersonMobile,jdbcType=VARCHAR}


   and business_license_num = #{businessLicenseNum,jdbcType=VARCHAR}


   and card_type = #{cardType,jdbcType=VARCHAR}


  and  card_num = #{cardNum,jdbcType=VARCHAR}


  and  create_timer = #{createTimer,jdbcType=TIMESTAMP}


   and create_source = #{createSource,jdbcType=VARCHAR}


   and create_user_id = #{createUserId,jdbcType=INTEGER}


   and last_modefy_timer = #{lastModefyTimer,jdbcType=TIMESTAMP}


   and last_modefy_user_id = #{lastModefyUserId,jdbcType=INTEGER}


   and scores = #{scores,jdbcType=DECIMAL}

 and status = 1
