mapper.insert:
insert into m_merchant_shop (id, entity_inner_code, shop_inner_code, 
  shop_name, area, address, 
  create_timer, create_source, create_user_id, 
  last_modefy_timer, last_modefy_user_id, status
  )
values (#{id,jdbcType=INTEGER}, #{entityInnerCode,jdbcType=VARCHAR}, #{shopInnerCode,jdbcType=VARCHAR}, 
  #{shopName,jdbcType=VARCHAR}, #{area,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, 
  #{createTimer,jdbcType=TIMESTAMP}, #{createSource,jdbcType=VARCHAR}, #{createUserId,jdbcType=INTEGER}, 
  #{lastModefyTimer,jdbcType=TIMESTAMP}, #{lastModefyUserId,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}
  )

mapper.select:
select count(*) from m_merchant_shop


   and entity_inner_code = #{entityInnerCode,jdbcType=VARCHAR}


   and shop_inner_code = #{shopInnerCode,jdbcType=VARCHAR}


   and shop_name = #{shopName,jdbcType=VARCHAR}


   and area = #{area,jdbcType=VARCHAR}


   and address = #{address,jdbcType=VARCHAR}


   and create_timer = #{createTimer,jdbcType=TIMESTAMP}


   and create_source = #{createSource,jdbcType=VARCHAR}


   and create_user_id = #{createUserId,jdbcType=INTEGER}


   and last_modefy_timer = #{lastModefyTimer,jdbcType=TIMESTAMP}


  and  last_modefy_user_id = #{lastModefyUserId,jdbcType=INTEGER}


   and status = #{status,jdbcType=INTEGER}
