mapper.select:
		SELECT
		TPC.*,CO.mer_name
		FROM
		(
		SELECT
		TP.*,C.channel_type,C.channel_mer_id,C.channel_mer_key,C.create_time,C.modify_time,C.modify_user_id
		FROM
		(
		SELECT
		T.*,
		P.channel_id,
		P.pos_name,
		P.sn_code,
		P.pos_factory,
		P.pos_type,
		P.status,
		P.bank_id,
		P.merc_refer_name,
		P.pos_addr,
		P.pos_province,
		P.pos_city,
		P.pos_area
		FROM
		(SELECT * from m_merchant_terminal


				AND id = #{condition.id ,jdbcType=INTEGER}


				AND inner_code = #{condition.innerCode ,jdbcType=VARCHAR}


				AND inner_code in (
         		SELECT inner_code FROM m_merchant_core WHERE mer_name LIKE CONCAT('%',#{condition.merName ,jdbcType=VARCHAR},'%') 
        		)


				AND inner_code in (
         		SELECT inner_code FROM m_merchant_channel WHERE channel_mer_id = #{condition.channelMerId ,jdbcType=VARCHAR} 
        		)


				AND pos_id = #{condition.posId ,jdbcType=INTEGER}


				AND pos_id in (
         		SELECT id FROM m_merchant_pos WHERE sn_code = #{condition.snCode ,jdbcType=VARCHAR} 
        		)


				AND terminal_code = #{condition.terminalCode ,jdbcType=VARCHAR}


				AND channel_terminal_code = #{condition.innerTermCode
				,jdbcType=VARCHAR}


				AND terminal_batch = #{condition.terminalBatch ,jdbcType=VARCHAR}


				AND terminal_para = #{condition.terminalPara ,jdbcType=VARCHAR}


				AND charges_type = #{condition.chargesType ,jdbcType=INTEGER}


				AND debit_card_rate = #{condition.debitCardRate ,jdbcType=VARCHAR}


				AND credit_card_rate = #{condition.creditCardRate ,jdbcType=VARCHAR}


				AND debit_card_fee = #{condition.debitCardFee ,jdbcType=INTEGER}


				AND credit_card_fee = #{condition.creditCardFee ,jdbcType=INTEGER}


				AND debit_card_max_fee = #{condition.debitCardMaxFee
				,jdbcType=INTEGER}


				AND credit_card_max_fee = #{condition.creditCardMaxFee
				,jdbcType=INTEGER}


				AND deal_switch = #{condition.dealSwitch ,jdbcType=VARCHAR}


				AND record_state = #{condition.recordState ,jdbcType=VARCHAR}


				AND term_audit_state = #{condition.termAuditState ,jdbcType=VARCHAR}


				AND term_name = #{condition.termName ,jdbcType=VARCHAR}


				AND wechat_fee = #{condition.wechatFee ,jdbcType=VARCHAR}


				AND alipay_fee = #{condition.alipayFee ,jdbcType=VARCHAR}


				AND terminal_type = #{condition.terminalType ,jdbcType=VARCHAR}


		) AS T
		LEFT JOIN m_merchant_pos AS P ON T.pos_id = P.id
		) AS TP
		LEFT JOIN m_merchant_channel AS C ON TP.channel_id = C.id
		) AS TPC
		LEFT JOIN m_merchant_core AS CO ON TPC.inner_code = CO.inner_code
		order by id desc
		limit #{startRow} , #{perPageSize}

		SELECT count(*)
		FROM
		(
		SELECT
		TP.*,C.channel_type,C.channel_mer_id,C.channel_mer_key,C.create_time,C.modify_time,C.modify_user_id
		FROM
		(
		SELECT
		T.*,
		P.channel_id,
		P.pos_name,
		P.sn_code,
		P.pos_factory,
		P.pos_type,
		P. STATUS,
		P.bank_id,
		P.merc_refer_name,
		P.pos_addr,
		P.pos_province,
		P.pos_city,
		P.pos_area
		FROM
		(SELECT * from m_merchant_terminal


				AND id = #{id ,jdbcType=INTEGER}


				AND inner_code = #{innerCode ,jdbcType=VARCHAR}


				AND inner_code in (
         		SELECT inner_code FROM m_merchant_core WHERE mer_name LIKE CONCAT('%',#{merName ,jdbcType=VARCHAR},'%') 
        		)


				AND inner_code in (
         		SELECT inner_code FROM m_merchant_channel WHERE channel_mer_id = #{channelMerId ,jdbcType=VARCHAR} 
        		)


				AND pos_id = #{posId ,jdbcType=INTEGER}


				AND pos_id in (
         		SELECT id FROM m_merchant_pos WHERE sn_code = #{snCode ,jdbcType=VARCHAR} 
        		)


				AND terminal_code = #{terminalCode ,jdbcType=VARCHAR}


				AND channel_terminal_code = #{innerTermCode ,jdbcType=VARCHAR}


				AND terminal_batch = #{terminalBatch ,jdbcType=VARCHAR}


				AND terminal_para = #{terminalPara ,jdbcType=VARCHAR}


				AND charges_type = #{chargesType ,jdbcType=INTEGER}


				AND debit_card_rate = #{debitCardRate ,jdbcType=VARCHAR}


				AND credit_card_rate = #{creditCardRate ,jdbcType=VARCHAR}


				AND debit_card_fee = #{debitCardFee ,jdbcType=INTEGER}


				AND credit_card_fee = #{creditCardFee ,jdbcType=INTEGER}


				AND debit_card_max_fee = #{debitCardMaxFee ,jdbcType=INTEGER}


				AND credit_card_max_fee = #{creditCardMaxFee ,jdbcType=INTEGER}


				AND deal_switch = #{dealSwitch ,jdbcType=VARCHAR}


				AND record_state = #{recordState ,jdbcType=VARCHAR}


				AND term_audit_state = #{termAuditState ,jdbcType=VARCHAR}


				AND term_name = #{termName ,jdbcType=VARCHAR}


				AND wechat_fee = #{wechatFee ,jdbcType=VARCHAR}


				AND alipay_fee = #{alipayFee ,jdbcType=VARCHAR}


				AND terminal_type = #{terminalType ,jdbcType=VARCHAR}


		) AS T
		LEFT JOIN m_merchant_pos AS P ON T.pos_id = P.id
		) AS TP
		LEFT JOIN m_merchant_channel AS C ON TP.channel_id = C.id
		) AS TPC
		LEFT JOIN m_merchant_core AS CO ON TPC.inner_code = CO.inner_code
