mapper.select:
     SELECT
			trade_hour AS tradeHour,
			SUM(turnover) AS turnover,
			SUM(order_num) AS orderNum
		FROM
			r_trade_by_hour


		        AND trade_date >= #{startTradeDate}


		        AND trade_date <= #{endTradeDate}


		        AND trade_date= #{tradeDate}


		       AND inner_code in (
		         SELECT inner_code FROM u_app_user_merchant WHERE app_user_id=#{userId}

		           AND role_id = #{roleId}

		       )


		      AND inner_code = #{innerCode}


		GROUP BY trade_hour

mapper.delete:
DELETE FROM r_trade_by_hour 


     AND trade_date >= #{startTradeDate}


    AND trade_date <  #{endTradeDate}


    AND trade_date= #{tradeDate}
