mapper.insert:
insert into m_merchant_file_temp (id, inner_code, file_name, 
  file_type, file_path, create_time
  )
values (#{id,jdbcType=INTEGER}, #{innerCode,jdbcType=VARCHAR}, #{fileName,jdbcType=VARCHAR}, 
  #{fileType,jdbcType=VARCHAR}, #{filePath,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select 

from m_merchant_file_temp


   AND inner_code = #{innerCode,jdbcType=VARCHAR}


   AND file_name = #{fileName,jdbcType=VARCHAR}


   AND file_type = #{fileType,jdbcType=VARCHAR}


   AND file_path = #{filePath,jdbcType=VARCHAR}


   AND create_time = #{createTime,jdbcType=TIMESTAMP}
