mapper.select:
SELECT SA.*,SU.name as modifyUser FROM (
select 

  from sys_msg_app


    AND send_time = #{condition.sendTime,jdbcType=TIMESTAMP}


    AND msg_subject = #{condition.msgSubject,jdbcType=VARCHAR}


    AND image_url = #{condition.imageUrl,jdbcType=VARCHAR}


    AND send_type = #{condition.sendType,jdbcType=INTEGER}


    AND push_type = #{condition.pushType,jdbcType=INTEGER}


    AND status = #{condition.status,jdbcType=INTEGER}


    AND modify_user_id = #{condition.modifyUserId,jdbcType=INTEGER}


    AND modify_time = #{condition.modifyTime,jdbcType=TIMESTAMP}


    AND msg_type = #{condition.msgType,jdbcType=INTEGER}


    AND bus_type = #{condition.busType,jdbcType=INTEGER}


    AND detail_url = #{condition.detailUrl,jdbcType=VARCHAR}


    AND msg_sub_title = #{condition.msgSubTitle,jdbcType=VARCHAR}



    	AND send_time >= #{condition.startSendTime}



    	AND send_time <= #{condition.endSendTime}



 ORDER BY modify_time DESC 
 limit #{startRow} , #{perPageSize}
 ) SA LEFT JOIN sys_user SU ON SA.modify_user_id = SU.ID

select count(*)  from sys_msg_app


    AND send_time = #{sendTime,jdbcType=TIMESTAMP}


    AND msg_subject = #{msgSubject,jdbcType=VARCHAR}


    AND image_url = #{imageUrl,jdbcType=VARCHAR}


    AND send_type = #{sendType,jdbcType=INTEGER}


    AND push_type = #{pushType,jdbcType=INTEGER}


    AND status = #{status,jdbcType=INTEGER}


    AND modify_user_id = #{modifyUserId,jdbcType=INTEGER}


    AND modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    AND msg_type = #{msgType,jdbcType=INTEGER}


    AND bus_type = #{busType,jdbcType=INTEGER}


    AND detail_url = #{detailUrl,jdbcType=VARCHAR}


    AND msg_sub_title = #{msgSubTitle,jdbcType=VARCHAR}



    	AND send_time >= #{startSendTime}



    	AND send_time <= #{endSendTime}

select 

  from sys_msg_app


    AND send_time = #{sendTime,jdbcType=TIMESTAMP}


    AND msg_subject = #{msgSubject,jdbcType=VARCHAR}


    AND image_url = #{imageUrl,jdbcType=VARCHAR}


    AND send_type = #{sendType,jdbcType=INTEGER}


    AND push_type = #{pushType,jdbcType=INTEGER}


    AND status = #{status,jdbcType=INTEGER}


    AND modify_user_id = #{modifyUserId,jdbcType=INTEGER}


    AND modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    AND msg_type = #{msgType,jdbcType=INTEGER}


    AND bus_type = #{busType,jdbcType=INTEGER}


    AND detail_url = #{detailUrl,jdbcType=VARCHAR}


    AND msg_sub_title = #{msgSubTitle,jdbcType=VARCHAR}


 ORDER BY send_time DESC 
 limit 1000

	  select 

	      from sys_msg_app where id in 

            #{msgId}

mapper.update:
 update sys_msg_app
	 set status = #{status,jdbcType=INTEGER}
 where id = #{id,jdbcType=INTEGER} and status = 0
