mapper.select:
select 

from sys_config


    AND type = #{type,jdbcType=VARCHAR}


    AND group_name = #{group,jdbcType=VARCHAR}


    AND name = #{name,jdbcType=VARCHAR}


    AND value = #{value,jdbcType=VARCHAR}


    AND status = #{status,jdbcType=INTEGER}


    AND remark = #{remark,jdbcType=VARCHAR}


LIMIT 1

