mapper.insert:
insert into sys_suggest (id, type, content, 
  app_user_id, mobile, submit_time, 
  reply_user_id, reply_time, state
  )
values (#{id,jdbcType=INTEGER}, #{type,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR}, 
  #{appUserId,jdbcType=INTEGER}, #{mobile,jdbcType=VARCHAR}, #{submitTime,jdbcType=TIMESTAMP}, 
  #{replyUserId,jdbcType=VARCHAR}, #{replyTime,jdbcType=TIMESTAMP}, #{state,jdbcType=VARCHAR}
  )

mapper.select:
		SELECT
			sys_suggest.*, m_app_user.user_name
		FROM
			sys_suggest
		LEFT JOIN m_app_user ON sys_suggest.app_user_id = m_app_user.id


        	AND submit_time >= #{condition.startTime,jdbcType=TIMESTAMP}



        	AND submit_time <= #{condition.endTime,jdbcType=TIMESTAMP}



		order BY submit_time desc
	limit #{startRow},#{perPageSize}

	 SELECT COUNT(*) 
		FROM
		sys_suggest


        	AND submit_time >= #{startTime,jdbcType=VARCHAR}



        	AND submit_time <= #{endTime,jdbcType=VARCHAR}
