mapper.update:
update r_trade_date_temp
set inner_code = #{innerCode,jdbcType=VARCHAR},
  amt = #{amt,jdbcType=VARCHAR},
  pay_sub_type = #{paySubType,jdbcType=VARCHAR},
  time_stamp = #{timeStamp,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT SUM(amt) AS turnover, COUNT(*) AS orderNum FROM t_trade_data WHERE inner_code IN ( SELECT inner_code FROM u_app_user_merchant WHERE app_user_id = #{userId}
AND role_id = #{roleId}
) AND time_stamp > #{tradeDate} AND status = 1
